import { useEffect, useState } from "react";
import { IOgrenci } from "../model/IOgrenci";
import axiosconfig from "../utils/axiosconfig";
import { Card, Row, Spinner, Stack } from "react-bootstrap";
import { NavLink } from "react-router-dom";

export default function OgrenciDetayComponent() {
  const [ogrenci, setOgrenci] = useState<IOgrenci>();

  // react router ile aşağıdaki gibi alınabilir ? ile gelen parametreler
  // let [searchParams, setSearchParams] = useSearchParams();
  // let ogrId = searchParams.get("ogrId");

  let ogrenciId = new URLSearchParams(window.location.search).get("ogrenciId");

  useEffect(() => {
    axiosconfig.get("ogrenci/getbyid/" + ogrenciId).then((response) => {
      // console.log("ogrenci API Yanıtı:", response.data);
      try {
        setOgrenci(response.data);
      } catch (err) {}
    });
  }, []);

  return (
    <div>
      {ogrenci != null ? (
        <Stack gap={3}>
          <Card>
            <Card.Body className="shadow">
              <Card.Title>{ogrenci.name}</Card.Title>
              <Card.Text>Öğrenci adı:{ogrenci.name} </Card.Text>
              <Card.Text>Öğrenci yılı:{ogrenci.year} </Card.Text>
            </Card.Body>
          </Card>
          <NavLink className="btn btn-danger" to="/ogrenci">
            Geri
          </NavLink>
        </Stack>
      ) : (
        <Row className="justify-content-center">
          <Spinner animation="border" variant="primary"></Spinner>
        </Row>
      )}
    </div>
  );
}
